COL release_name NEW_V fa_release FOR A20;
var fa_release varchar2(50)
exec :fa_release := 'n/a';
declare
 l_fa varchar2(1);
begin
  SELECT 'Y' 
    INTO l_fa
    FROM dba_tab_columns
   WHERE table_name = 'AD_PRODUCT_GROUPS'
     AND column_name = 'RELEASE_NAME'
     AND data_type = 'VARCHAR2'
     AND owner = 'FUSION'
  ;

  IF l_fa = 'Y' THEN
