   AND h.snap_id = s.snap_id
   AND h.dbid = s.dbid
   AND h.instance_number = s.instance_number
   AND :license IN ('T', 'D', 'L')
 ORDER BY
       s.end_interval_time,
       h.instance_number,
       h.plan_hash_value ) v;

