PRO <!-- Please Wait -->	

SELECT /* ^^script..sql Table Partitions */
       v2.line_text
  FROM (
--Uday.PSR.v6WITH object AS (
--Uday.PSR.v6SELECT /*+ MATERIALIZE */
--Uday.PSR.v6       object_owner owner, object_name name, object_type
--Uday.PSR.v6  FROM gv$sql_plan
