--Uday.PSR.v6   FROM dba_indexes i,
--Uday.PSR.v6        dba_ind_partitions p,
--Uday.PSR.v6        object o
--Uday.PSR.v6  WHERE i.owner = o.owner
--Uday.PSR.v6    AND o.object_type like 'INDEX%'  --Uday.v6
--Uday.PSR.v6    AND i.index_name = o.name
