	   '<th>View<br>Related</th>'||CHR(10)||
       '</tr>'||CHR(10) line_text
  FROM plan_table
 WHERE object_type = 'TABLE'
   AND STATEMENT_ID = :sql_id
 UNION ALL
SELECT object_name table_name,
       object_owner owner,
       4 line_type,
       1 row_num,
       CHR(10)||'</table></details><!--Pushkar-->'||CHR(10)||CHR(10) line_text
  FROM plan_table
 WHERE object_type = 'TABLE'
