--20210804
--BEGIN
--  SELECT
--   NVL( (SELECT 1 FROM GV$SQL_SHARED_CURSOR SC
--         WHERE SC.AUTH_CHECK_MISMATCH = 'Y' AND ROWNUM = 1 AND SC.SQL_ID = :L_SQL_ID), 0)
--  INTO :L_VPD_CNT
--  FROM DUAL;
--END;
