 :L_CUR_NR  := 0 ;
 :L_CUR_KEY := 0 ;
 :L_CUR_PHV := 0 ;
 :L_CUR_OID := 0 ;

 :L_PRV_KEY  := 0 ;
 :L_PRV_PHV  := 0 ;
 :L_PRV_ID   := 0 ;
 :L_PRV_OID  := 0 ;
 :L_PRV_PID  := 0 ;
 :L_PRV_NR   := 0 ;
 :L_PRV_OBJECT       := null ;
 :L_PRV_OBJECT_OWNER := null ;
 :L_PRV_OPERATION    := null ;
 :L_PRV_QBLOCK       := null ;
 :L_PRV_ALIAS        := null ;
 :L_PRV_OBJECT_TYPE  := null ;

 :L_PPRV_KEY        := 0 ;
 :L_PPRV_PHV        := 0 ;
 :L_PPRV_ID         := 0 ;
 :L_PPRV_OID        := 0 ;
 :L_PPRV_PID        := 0 ;
 :L_PPRV_NR         := 0 ;
 :L_PPRV_OBJECT      := null ;
 :L_PPRV_OBJECT_OWNER := null ;
