                                           AND TO_CHAR(SAMPLE_TIME,'YYYY-MM-DD HH24:MI:SS') <= SM.START_TIME )
                                          , to_date('1000-01-01 00:00:01','YYYY-MM-DD HH24:MI:SS')
                                        )
