                  :L_WHERE_LOG := 'TEST ESTIMATION'||:L_last_load_time;
                  IF ( to_date(:L_last_load_time,'YYYY-MM-DD HH24:MI:SS') = to_date('1000-01-01 00:00:01','YYYY-MM-DD HH24:MI:SS') ) THEN
