             :l_as3 := :l_as2;
             :l_as2 := :l_as1;
             :l_as1 := spm.mo;
             :l_OCC5 := :l_OCC4;
             :l_OCC4 := :l_OCC3;
             :l_OCC3 := :l_OCC2;
             :l_OCC2 := :l_OCC1;
             :l_OCC1 := spm.ON_CPU_COUNT;
             :l_WC5 := :l_WC4;
             :l_WC4 := :l_WC3;
             :l_WC3 := :l_WC2;
