          IF (spm.WORKAREA_MAX_TEMPSEG IS NULL or spm.WORKAREA_MAX_TEMPSEG=0)       THEN :L_TEMP:=NULL; ELSE :L_TEMP:='<RED> TEMP:'||spm.TEMP_MB||'M</RED>';  END IF;
