               :l_co1-1,:l_co2-1,:l_co3-1,:l_co4-1,:l_co5-1,
               :l_co1,  :l_co2,  :l_co3,  :l_co4,  :l_co5,
               :l_co1+1,:l_co2+1,:l_co3+1,:l_co4+1,:l_co5+1,
               --:l_co1+2,:l_co2+2,:l_co3+2,:l_co4+2,:l_co5+2,
