':'||SUBSTR(regexp_substr(P.ACCESS_PREDICATES,':PSR\S+',1),INSTR(regexp_substr(P.ACCESS_PREDICATES,':PSR\S+',1),'__')+2,LENGTH(regexp_substr(P.ACCESS_PREDICATES,':PSR\S+',1)))
  ),'TO_NUMBER(:', ':'),'TO_CHAR(:', ':') aACCESS_PREDICATES,
ACCESS_PREDICATES,
