        order by to_DATE(to_CHAR(CSH.SAVTIME,'yyyy-mm-dd hh24:mi:ss'),'yyyy-mm-dd hh24:mi:ss') desc FETCH FIRST 1 ROW ONLY ) HIGH_VALUE,
        SM.NAME BIND_NAME,
        substr(
