     --(SELECT obj.owner||'.'||obj.object_name||NVL2(obj.subobject_name, '.'||obj.subobject_name, NULL)
     --         FROM dba_objects obj WHERE obj.object_id = current_obj#) OBJ_NAME,
              --current_obj#,
