IF ( :L_SHORT_DB_VERSION LIKE '19%' or :L_SHORT_DB_VERSION LIKE '2%') THEN
-- SMA19C
 IF ( :L_CURRENT_WORST1 <> -1 ) THEN
    --DBMS_OUTPUT.PUT_LINE('.');
