/* Pushkar - Release the lock so that other SQLHCs can run */
DECLARE
   l_release_retval number;
   l_str varchar2(200);
BEGIN
    l_str := q'[begin :1 := dbms_lock.release(lockhandle => :2); end;]';
    execute immediate l_str using OUT l_release_retval, IN :lockhandle;

EXCEPTION
    WHEN OTHERS THEN
       NULL;
END;
